import react from 'react';

// trigger login by enter key
loginOnEnterKey = (e) => {
    if (e.keyCode === 13 && this.state.valid) {
        document.querySelector('#login-btn').click();
        componentWillUnmount();
    }
}

// add keyup event on mount
function componentDidMount() {
    document.addEventListener('down', loginOnEnterKey);
}

// remove keyup event on unmount
function componentWillUnmount() {
    document.removeEventListener('keyup', loginOnEnterKey);
}



const Login = (props) => {

    const {email, 
        setEmail, 
        password, 
        setPassword, 
        handleLogin, 
        handleLogout, 
        emailError, 
        passwordError,
        user,
    } = props;

    componentDidMount();

    return (
        <section className="login">
            <div className="loginContainer">
                <label>Username</label>
                <input type="text" autoFocus required value={email} onChange={(e) => setEmail(e.target.value)}></input>
                <p className="errorMsg">{emailError}</p>

                <label>Password</label>
                <input type="password" autoFocus required value={password} onChange={(e) => setPassword(e.target.value)}></input>
                <p className="errorMsg">{passwordError}</p>
                <div className="btnContainer">
                <button id="login-btn" onKey={loginOnEnterKey} onClick={handleLogin}>Sign in</button>
            </div>
            </div>

        </section>
    )
}

export default Login;
