import react from 'react';

// trigger login by enter key
const loginOnEnterKey = (e) => {
    if (e.keyCode === 13) {
        document.getElementById('#login-btn').click();
        componentWillUnmount();
    }
}





const Login = (props) => {

    const {email, 
        setEmail, 
        password, 
        setPassword, 
        handleLogin, 
        handleLogout, 
        emailError, 
        passwordError,
        user,
    } = props;

    const handleKeypress = (e) => {
        if(e.keyCode === 13){
            handleLogin();
        }
    }

    return (
        <section className="login">
            <div className="loginContainer">
                <label>Username</label>
                <input type="text" autoFocus required value={email} onChange={(e) => setEmail(e.target.value)}></input>
                <p className="errorMsg">{emailError}</p>

                <label>Password</label>
                <input type="password" required value={password} onChange={(e) => setPassword(e.target.value)}></input>
                <p className="errorMsg">{passwordError}</p>
                <div className="btnContainer">
                <button id="login-btn" onKeyPress={handleKeypress} onClick={handleLogin}>Sign in</button>
            </div>
            </div>

        </section>
    )
}

export default Login;
