import {
    Redirect,
    Route,
    Switch,
    useLocation,
    withRouter,
  } from 'react-router-dom';
  import React, { useEffect } from "react";
  import Hero from '../components/Hero'
  import Login from '../components/Login'
  import fire from '../fire'


  const Routes = ( props ) => {
    const { pathname } = useLocation();

    const { user } = props;

    useEffect(() => {
      window.scrollTo(0, 0);
    }, [pathname]);

    console.log(user);

    return (
        <Switch>
            <Route exact path="/">
            {user ? <Redirect to="/" component={Hero}/> : <Redirect to="/Login" component={Login}/>}
            </Route>
        </Switch>
    );
  };

  export default withRouter(Routes);


